// WeatherWidget.js

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './WeatherWidget.css';

const WeatherWidget = () => {
  const [weatherData, setWeatherData] = useState(null);

  useEffect(() => {
    // Replace 'YOUR_API_KEY' with your actual WeatherAPI key
    axios.get(`https://api.weatherapi.com/v1/current.json?key=YOUR_API_KEY&q=London`)
      .then(response => {
        setWeatherData(response.data);
      })
      .catch(error => {
        console.error('Error fetching weather data:', error);
      });
  }, []);

  if (!weatherData) {
    return <div>Loading...</div>;
  }

  return (
    <div className="weather-widget">
      <h2>{weatherData.location.name}</h2>
      <p>{weatherData.current.condition.text}</p>
      <p>Temperature: {weatherData.current.temp_c}�C</p>
      <p>Feels like: {weatherData.current.feelslike_c}�C</p>
      <p>Humidity: {weatherData.current.humidity}%</p>
      <p>Wind Speed: {weatherData.current.wind_kph} kph</p>
      <p>UV Index: {weatherData.current.uv}</p>
      <p>Visibility: {weatherData.current.vis_km} km</p>
      <p>Sunrise: {weatherData.forecast.forecastday[0].astro.sunrise}</p>
      <p>Sunset: {weatherData.forecast.forecastday[0].astro.sunset}</p>
    </div>
  );
};

export default WeatherWidget;
