import React, { useEffect, useState } from 'react';

function StockWidget() {
  const [stockData, setStockData] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Create a new WebSocket connection
    const ws = new WebSocket('wss://ws.finnhub.io?token=cqh3schr01qompnrrj70cqh3schr01qompnrrj7g');

    ws.onopen = () => {
      console.log('WebSocket connection opened');
      // Send subscription messages
      ws.send(JSON.stringify({ type: 'subscribe', symbol: 'AAPL' }));
      ws.send(JSON.stringify({ type: 'subscribe', symbol: 'AMZN' }));
      ws.send(JSON.stringify({ type: 'subscribe', symbol: 'BINANCE:BTCUSDT' }));
      ws.send(JSON.stringify({ type: 'subscribe', symbol: 'IC MARKETS:1' }));
    };

    ws.onmessage = (event) => {
      const data = JSON.parse(event.data);
      console.log('Stock data received:', data); // Log received data
      setStockData(prevData => [...prevData, data]); // Update state with new data
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      setError('Failed to fetch stock data');
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    // Cleanup function
    return () => {
      ws.close();
    };
  }, []);

  if (error) return <div>{error}</div>;
  if (stockData.length === 0) return <div>Loading...</div>;

  return (
    <div>
      <h2>Stock Data</h2>
      {stockData.map((data, index) => (
        <div key={index}>
          <h3>{data.s}</h3>
          <p>Price: {data.p}</p>
          <p>Volume: {data.v}</p>
          {/* Adjust the above fields based on actual WebSocket data */}
        </div>
      ))}
    </div>
  );
}

export default StockWidget;
