import React, { useEffect, useState } from 'react';

function WeatherWidget() {
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://api.weatherapi.com/v1/current.json?key=da5d90204be742a28ae115507242507&q=Bengaluru&aqi=no')
      .then(response => response.json())
      .then(data => {
        console.log('Weather data fetched:', data); // Log the fetched data
        setWeatherData(data);
      })
      .catch(error => {
        console.error('Error fetching weather data:', error); // Log any errors
        setError('Failed to fetch weather data');
      });
  }, []);

  if (error) return <div>{error}</div>;
  if (!weatherData) return <div>Loading...</div>;

  return (
    <div>
      <h2>Weather in {weatherData.location.name}</h2>
      <p>Location: {weatherData.location.name}, {weatherData.location.region}, {weatherData.location.country}</p>
      <p>Temperature: {weatherData.current.temp_c}°C ({weatherData.current.temp_f}°F)</p>
      <p>Condition: {weatherData.current.condition.text}</p>
      <img src={`https:${weatherData.current.condition.icon}`} alt={weatherData.current.condition.text} />
      <p>Wind: {weatherData.current.wind_kph} kph ({weatherData.current.wind_mph} mph)</p>
      <p>Humidity: {weatherData.current.humidity}%</p>
      <p>Pressure: {weatherData.current.pressure_mb} mb</p>
      <p>Precipitation: {weatherData.current.precip_mm} mm</p>
    </div>
  );
}

export default WeatherWidget;
