import React, { useEffect, useState } from 'react';
import './WeatherWidget.css'; 

function WeatherWidget() {
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetch('http://api.weatherapi.com/v1/current.json?key=da5d90204be742a28ae115507242507&q=Bengaluru&aqi=no')
      .then(response => response.json())
      .then(data => {
        console.log('Weather data fetched:', data); // Log the fetched data
        setWeatherData(data);
      })
      .catch(error => {
        console.error('Error fetching weather data:', error); // Log any errors
        setError('Failed to fetch weather data');
      });
  }, []);

  if (error) return <div className="weather-card error">{error}</div>;
  if (!weatherData) return <div className="weather-card">Loading...</div>;

  return (
    <div className="weather-card">
      <img src={`https:${weatherData.current.condition.icon}`} alt={weatherData.current.condition.text} />
      <h2>Weather in {weatherData.location.name}</h2>
      <p className="location">
        {weatherData.location.name}, {weatherData.location.region}, {weatherData.location.country}
      </p>
      <p className="temperature">
        {weatherData.current.temp_c}°C ({weatherData.current.temp_f}°F)
      </p>
      <p>Condition: {weatherData.current.condition.text}</p>
      <p>Wind: {weatherData.current.wind_kph} kph ({weatherData.current.wind_mph} mph)</p>
      <p>Humidity: {weatherData.current.humidity}%</p>
      <p>Pressure: {weatherData.current.pressure_mb} mb</p>
      <p>Precipitation: {weatherData.current.precip_mm} mm</p>

      {/* Add buttons for weather links */}
      <a href="https://www.accuweather.com/" target="_blank" rel="noopener noreferrer" className="button-accuweather">AccuWeather</a>
      <a href="https://weather.com/en-TO/weather/today/l/TNXX0001:1:TN" target="_blank" rel="noopener noreferrer" className="button-weather-com">Weather.com</a>

      {/* Add footer text */}
      <div className="weather-footer">Designed by SS</div>
    </div>
  );
}

export default WeatherWidget;
